mapper.select:
    SELECT * FROM lectures A INNER JOIN users B ON A.teacherid = B.uid
    
    	lid = #{lid}
		 AND lname = #{lname}
		 AND YMD_STD = #{YMD_STD}
		 AND YMD_END = #{YMD_END}
		 AND times = #{times}
		 AND days = #{days}
		 AND hours = #{hours}
		 AND place = #{place}
		 AND target = #{target}
		 AND teacherID = #{teacherID}
		 AND credit = #{credit}
		 AND ltype = #{ltype}
		 AND institution = #{institution}
		 AND curriculum = #{curriculum}
		 AND approval = #{approval}
    	AND uname = #{uname}
    	AND utype = #{utype}
    	AND team = #{team}
    	AND rank = #{rank}
